contracts/AttestorRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";

contract AttestorRegistry is Ownable {
    struct Attestor {
        bool isRegistered;
        uint256 stake;
    }

    mapping(address => Attestor) public attestors;
    // claimId => attestor => hasAttested
    mapping(uint256 => mapping(address => bool)) public hasAttested;
    // claimId => totalStake
    mapping(uint256 => uint256) public totalStakePerClaim;

    event AttestorRegistered(address indexed attestor);
    event StakeAdded(address indexed attestor, uint256 amount);
    event ClaimAttested(uint256 indexed claimId, address indexed attestor, uint256 stakeAmount);
    event AttestorSlashed(address indexed attestor, uint256 amount, address indexed slasher);

    constructor() Ownable(msg.sender) {}

    /**
     * @notice Registers the caller as an attestor. Optionally accepts initial stake.
     */
    function register() external payable {
        require(!attestors[msg.sender].isRegistered, "AttestorRegistry: already registered");

        attestors[msg.sender].isRegistered = true;

        emit AttestorRegistered(msg.sender);

        if (msg.value > 0) {
            _stakeETH(msg.sender, msg.value);
        }
    }

    /**
     * @notice Allows a registered attestor to add more ETH stake.
     */
    function stakeETH() external payable {
        require(attestors[msg.sender].isRegistered, "AttestorRegistry: not registered");
        require(msg.value > 0, "AttestorRegistry: no value sent");

        _stakeETH(msg.sender, msg.value);
    }

    function _stakeETH(address staker, uint256 amount) internal {
        attestors[staker].stake += amount;
        emit StakeAdded(staker, amount);
    }

    /**
     * @notice Attests to a specific claim ID.
     * @param claimId The ID of the claim to attest to.
     */
    function attestToClaim(uint256 claimId) external {
        require(attestors[msg.sender].isRegistered, "AttestorRegistry: not registered");
        require(attestors[msg.sender].stake > 0, "AttestorRegistry: no stake");
        require(!hasAttested[claimId][msg.sender], "AttestorRegistry: already attested");

        hasAttested[claimId][msg.sender] = true;
        uint256 stakeAmount = attestors[msg.sender].stake;
        totalStakePerClaim[claimId] += stakeAmount;

        emit ClaimAttested(claimId, msg.sender, stakeAmount);
    }

    /**
     * @notice Admin-only function to slash an attestor's stake.
     * @param attestor The address of the attestor to slash.
     * @param amount The amount of ETH to slash.
     */
    function slash(address attestor, uint256 amount) external onlyOwner {
        require(attestors[attestor].stake >= amount, "AttestorRegistry: insufficient stake");

        attestors[attestor].stake -= amount;

        // Transfer slashed funds to the owner
        (bool sent, ) = owner().call{value: amount}("");
        require(sent, "AttestorRegistry: failed to send slashed funds");

        emit AttestorSlashed(attestor, amount, msg.sender);
    }
}

contracts/YieldProof.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AttestorRegistry.sol";

/**
 * @title YieldProof
 * @dev A simple Hackathon MVP contract for proving and tracking yield claims.
 *      Allows issuers to submit claims and an admin (mock governance) to update their status.
 */
contract YieldProof {
    // ============================
    // Type Definitions
    // ============================

    /// @notice Represents the current status of a submitted yield claim.
    enum ClaimStatus {
        Pending,    // Initial state after submission
        Attested,   // Verified by an off-chain or on-chain attestor (placeholder)
        Approved,   // Final approval by governance/admin
        Challenged  // Flagged as invalid or fraudulent
    }

    /// @notice variable struct holding all metadata for a specific yield claim.
    struct YieldClaim {
        uint256 claimId;        // Unique identifier for the claim
        string assetId;         // Identifier for the underlying asset (e.g., "USDC-LP-V3")
        string period;          // Time period covered (e.g., "2023-Q4")
        uint256 yieldAmount;    // The claimed yield amount (in smallest unit)
        string documentHash;    // IPFS hash or link to supporting documents/proofs
        address issuer;         // Address of the user submitting the claim
        ClaimStatus status;     // Current status of the claim
    }

    // ============================
    // State Variables
    // ============================

    /// @dev Counter for generating unique claim IDs.
    uint256 private nextClaimId;

    /// @notice Registry of all claims, mapped by their ID.
    mapping(uint256 => YieldClaim) public claims;

    /// @notice Governance/Admin address allowed to update statuses.
    address public admin;

    /// @notice Reference to the AttestorRegistry contract.
    AttestorRegistry public attestorRegistry;

    // ============================
    // Events
    // ============================

    /// @notice Emitted when a new yield claim is submitted.
    event YieldClaimSubmitted(
        uint256 indexed claimId,
        address indexed issuer,
        string assetId,
        uint256 yieldAmount
    );

    /// @notice Emitted when a claim's status is changed by the admin.
    event ClaimStatusUpdated(
        uint256 indexed claimId,
        ClaimStatus newStatus,
        address updatedBy
    );

    // ============================
    // Modifiers
    // ============================

    /// @dev Restricts access to the admin/governance address.
    modifier onlyAdmin() {
        require(msg.sender == admin, "YieldProof: caller is not the admin");
        _;
    }

    // ============================
    // Constructor
    // ============================

    constructor(address _attestorRegistry) {
        // Set the deployer as the initial admin
        admin = msg.sender;
        attestorRegistry = AttestorRegistry(_attestorRegistry);
    }

    // ============================
    // External Functions
    // ============================

    /**
     * @notice Allows an issuer to submit a new yield claim.
     * @dev The status is initialized to 'Pending'.
     * @param _assetId The string identifier of the asset (e.g., "MANTLE-ETH").
     * @param _period The string identifier for the period (e.g., "OCT-2023").
     * @param _yieldAmount The numeric amount of yield being claimed.
     * @param _documentHash An IPFS hash or URL pointing to proof documents.
     */
    function submitClaim(
        string calldata _assetId,
        string calldata _period,
        uint256 _yieldAmount,
        string calldata _documentHash
    ) external {
        uint256 claimId = nextClaimId;
        nextClaimId++;

        claims[claimId] = YieldClaim({
            claimId: claimId,
            assetId: _assetId,
            period: _period,
            yieldAmount: _yieldAmount,
            documentHash: _documentHash,
            issuer: msg.sender,
            status: ClaimStatus.Pending
        });

        emit YieldClaimSubmitted(claimId, msg.sender, _assetId, _yieldAmount);
    }

    /**
     * @notice Admin-only function to update the status of any claim.
     * @dev Acts as a mock governance mechanism for the hackathon MVP.
     * @param _claimId The ID of the claim to update.
     * @param _newStatus The new status to assign (0=Pending, 1=Attested, 2=Approved, 3=Challenged).
     */
    function updateClaimStatus(uint256 _claimId, ClaimStatus _newStatus) external onlyAdmin {
        // Ensure the claim exists (issuer should not be zero address)
        require(claims[_claimId].issuer != address(0), "YieldProof: claim does not exist");

        claims[_claimId].status = _newStatus;

        emit ClaimStatusUpdated(_claimId, _newStatus, msg.sender);
    }

    /**
     * @notice Updates the claim status based on AttestorRegistry data.
     * @dev This is a mock verification hook effectively.
     *      Anyone can call this to trigger the status update if conditions are met.
     * @param claimId The ID of the claim to check and update.
     */
    function updateClaimStatus(uint256 claimId) external {
        YieldClaim storage claim = claims[claimId];
        require(claim.issuer != address(0), "YieldProof: claim does not exist");
        require(claim.status == ClaimStatus.Pending, "YieldProof: claim not pending");

        // Check if there is any stake on this claim in the registry
        uint256 totalStake = attestorRegistry.totalStakePerClaim(claimId);

        if (totalStake > 0) {
            claim.status = ClaimStatus.Attested;
            emit ClaimStatusUpdated(claimId, ClaimStatus.Attested, msg.sender);
        }
    }

    /**
     * @notice Read-only helper to get the total number of claims submitted.
     */
    function getTotalClaims() external view returns (uint256) {
        return nextClaimId;
    }
}

contracts/YieldVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./AttestorRegistry.sol";
import "./YieldProof.sol";

/**
 * @title YieldVault
 * @dev A simple "RealFi" vault for the Hackathon MVP.
 *      This contract does NOT generate yield itself. Instead, it enforces policies:
 *      Uses funds are deposited here, but "yield" (or unlocking of funds/rewards) 
 *      is only permitted if a YieldProof claim meets the minimum stake threshold 
 *      in the AttestorRegistry.
 */
contract YieldVault {
    // ============================
    // State Variables
    // ============================

    YieldProof public yieldProof;
    AttestorRegistry public attestorRegistry;

    /// @notice Minimum total stake required on a claim to unlock yield/rewards.
    uint256 public minTotalStake;

    /// @notice Total ETH deposited in the vault.
    uint256 public totalDeposits;

    /// @notice User ETH balances.
    mapping(address => uint256) public balances;

    // ============================
    // Events
    // ============================

    event Deposited(address indexed user, uint256 amount);
    event Withdrawn(address indexed user, uint256 amount);
    event YieldUnlocked(uint256 indexed claimId, uint256 totalStake);

    // ============================
    // Constructor
    // ============================

    constructor(
        address _yieldProof,
        address _attestorRegistry,
        uint256 _minTotalStake
    ) {
        yieldProof = YieldProof(_yieldProof);
        attestorRegistry = AttestorRegistry(_attestorRegistry);
        minTotalStake = _minTotalStake;
    }

    // ============================
    // External Functions
    // ============================

    /**
     * @notice Deposits ETH into the vault.
     */
    function deposit() external payable {
        require(msg.value > 0, "YieldVault: cannot deposit 0");

        balances[msg.sender] += msg.value;
        totalDeposits += msg.value;

        emit Deposited(msg.sender, msg.value);
    }

    /**
     * @notice Withdraws ETH from the vault.
     * @dev In a full production version, this would check for locked funds 
     *      tied to active claims. For this MVP, it allows withdrawal specific logic 
     *      to be added later.
     * @param amount The amount of ETH to withdraw.
     */
    function withdraw(uint256 amount) external {
        require(balances[msg.sender] >= amount, "YieldVault: insufficient balance");

        balances[msg.sender] -= amount;
        totalDeposits -= amount;

        (bool sent, ) = msg.sender.call{value: amount}("");
        require(sent, "YieldVault: failed to send ETH");

        emit Withdrawn(msg.sender, amount);
    }

    /**
     * @notice Checks if a specific claim has enough attestor stake to be "unlocked".
     * @param claimId The ID of the claim to check.
     * @return true if the claim is sufficiently attested.
     */
    function canUnlockYield(uint256 claimId) public view returns (bool) {
        uint256 currentStake = attestorRegistry.totalStakePerClaim(claimId);
        return currentStake >= minTotalStake;
    }

    /**
     * @notice Unlocks yield/rewards for a claim if it meets the stake threshold.
     * @dev This is a simplified action. In a real protocol, this might 
     *      mint tokens, release escrowed funds, or distribute rewards to attestors.
     * @param claimId The ID of the claim to unlock.
     */
    function unlockYield(uint256 claimId) external {
        require(canUnlockYield(claimId), "YieldVault: insufficient stake to unlock");

        // NOTE: Actual distribution logic would go here.
        // For the MVP, we just emit the event to signal the off-chain indexer/UI.

        uint256 stake = attestorRegistry.totalStakePerClaim(claimId);
        emit YieldUnlocked(claimId, stake);
    }
}

